mapper.select:
select

from sys_topic


    id = #{id,jdbcType=BIGINT}


    AND name = #{name,jdbcType=VARCHAR}


    AND unique = #{unique,jdbcType=VARCHAR}


    AND menu2_id = #{menu2Id,jdbcType=BIGINT}


    AND menu3_id = #{menu3Id,jdbcType=BIGINT}


    AND status = #{status,jdbcType=TINYINT}
